options.rootCSS:
:root{
  --bg:#07070a;
  --panel:#0f1115cc;
  --glass: rgba(255,255,255,0.03);
  --accent-a: #bc3813;
  --accent-b: #7b61ff;
  --danger: #ff6b6b;
  --success: #49e07a;
  --muted: #9aa3b2;
