options.onRender:
// Pega os valores
const valuesAttackers = data.series[0].fields[1].values;

// Valores individuais
let dos = valuesAttackers[0] || 0;   // DDoS/DoS
let pts = valuesAttackers[1] || 0;   // PortScan
let bt  = valuesAttackers[2] || 0;   // BruteForce

// Calcular total
const total = dos + pts + bt;

// Evitar divisão por zero
const dosAngle = total > 0 ? (dos / total) * 360 : 0;
const ptsAngle = total > 0 ? (pts / total) * 360 : 0;
const btAngle  = total > 0 ? (bt  / total) * 360 : 0;

// Atualizar gráfico
const pieChart = htmlNode.getElementById('pieChart');
pieChart.style.background = `conic-gradient(
    #590004 0deg ${dosAngle}deg,
    #3A0001 ${dosAngle}deg ${dosAngle + ptsAngle}deg,
    #220001 ${dosAngle + ptsAngle}deg 360deg
)`;

// Atualizar tabela
htmlNode.getElementById('dosValue').textContent = dos;
htmlNode.getElementById('ddosValue').textContent = pts;
htmlNode.getElementById('ptsValue').textContent  = bt;

