options.onRender:
// Garante que data.series[0]?.fields exista
const valuesAttackers = data?.series?.[0]?.fields || [];

// Se não tiver valores, evita erro
if (valuesAttackers.length < 4) {
  console.warn("Sem dados para montar tabela MITRE");
} else {
  // Converte colunas em array de objetos
  const mitreTechnique = valuesAttackers[0].values.map((_, index) => ({
    tecnica: valuesAttackers[0].values?.[index] ?? "",
    id: valuesAttackers[1].values?.[index] ?? "",
    severidade: valuesAttackers[2].values?.[index] ?? "",
    detectado: valuesAttackers[3].values?.[index] ?? ""
  }));

  // Ordena do maior detectado para o menor (tratando caso vazio)
  mitreTechnique.sort((a, b) => (b.detectado || 0) - (a.detectado || 0));

  // Seleciona o tbody da tabela
  const tableBody = htmlNode.querySelector("#mitreTable tbody");
  tableBody.innerHTML = ""; // Limpa linhas antigas

  // Popula a tabela
  mitreTechnique.forEach(item => {
    const row = document.createElement("tr");
    row.innerHTML = `
      <td>${item.tecnica}</td>
      <td>${item.id}</td>
      <td>${item.severidade ? item.severidade + "/15" : ""}</td>
      <td>${item.detectado}</td>
    `;
    tableBody.appendChild(row);
  });
}

