  transition: background-color 0.3s;
}

options.onRender:
const btn = htmlNode.getElementById('scrollBtn');
let scrolling = false;
let direction = 1; // 1 = descer, -1 = subir
const speed = 1; // pixels por frame
let rafId = null;

function scrollStep() {
  const maxScroll = document.documentElement.scrollHeight - window.innerHeight;
  let nextPos = window.scrollY + direction * speed;

  if (nextPos >= maxScroll) {
    direction = -1; // inverter para subir
  } else if (nextPos <= 0) {
    direction = 1; // inverter para descer
  }

  window.scrollTo(0, nextPos);
  rafId = requestAnimationFrame(scrollStep);
}

btn.addEventListener('click', () => {
  if (!scrolling) {
    scrolling = true;
    btn.textContent = "Parar Scroll";

    // cancela qualquer loop antigo antes de começar um novo
    if (rafId) cancelAnimationFrame(rafId);

    scrollStep();
  } else {
    scrolling = false;
    btn.textContent = "Iniciar Scroll";
    cancelAnimationFrame(rafId);
    rafId = null;
  }
});
