options.onRender:
const namesAttackers = data.series[0].fields[0].values; 
const valuesAttackers = data.series[0].fields[1].values; 

let attacks = namesAttackers.map((name, index) => ({
  type: name,
  value: valuesAttackers[index]
}));

// ðŸ”¹ Ordena do maior para o menor
attacks.sort((a, b) => b.value - a.value);

// ðŸ”¹ Pega apenas os top 6
attacks = attacks.slice(0, 5);

const chart = htmlNode.getElementById('chart');

function renderChart(attacks){
  chart.innerHTML = '';
  const maxValue = Math.max(...attacks.map(d => d.value));

  attacks.forEach(item => {
    const barContainer = document.createElement('div');
    barContainer.className = 'bar-container';

    const valueDiv = document.createElement('div');
    valueDiv.className = 'value';
    valueDiv.textContent = item.value;

    const barDiv = document.createElement('div');
    barDiv.className = 'bar';
    barDiv.style.height = (item.value / maxValue * 200) + 'px'; // altura proporcional

    const labelDiv = document.createElement('div');
    labelDiv.className = 'label';
    labelDiv.textContent = item.type;

    barContainer.appendChild(valueDiv);
    barContainer.appendChild(barDiv);
    barContainer.appendChild(labelDiv);
    chart.appendChild(barContainer);
  });
}

renderChart(attacks);

