options.onRender:
// Garante que data.series[0]?.fields exista e tenha pelo menos duas colunas
const valuesAttackers = data.series?.[0]?.fields || [];

// Converte colunas em array de objetos, garantindo valores default 0 ou ""
const crawlersmap = (valuesAttackers[0]?.values || []).map((_, index) => ({
  name: valuesAttackers[0].values?.[index] ?? "N/A",
  count: valuesAttackers[1]?.values?.[index] ?? 0
}));

const crawlers = crawlersmap.map(item => ({
  name: item.name,
  count: Number(item.count) || 0 
}));

const chart = htmlNode.getElementById('crawlerChart');

function renderChart() {
  chart.innerHTML = '';
  
  // Ordena do maior para o menor
  crawlers.sort((a, b) => b.count - a.count);

  // Se nÃ£o houver dados, coloca um item default
  const displayCrawlers = crawlers.length ? crawlers : [{ name: "No crawler", count: 0 }];

  const maxCount = Math.max(...displayCrawlers.map(c => c.count)) || 1;

  displayCrawlers.forEach(crawler => {
    const barContainer = document.createElement('div');
    barContainer.className = 'bar';
    
    const label = document.createElement('div');
    label.className = 'bar-label';
    label.textContent = crawler.name;

    const value = document.createElement('div');
    value.className = 'bar-value';
    const widthPercent = (crawler.count / maxCount) * 100;
    value.style.width = widthPercent + '%';
    value.textContent = crawler.count;

    barContainer.appendChild(label);
    barContainer.appendChild(value);
    chart.appendChild(barContainer);
  });
}

renderChart();

